// Acessa a saída dos nós de IA e de entrada, especificando cada entrada pelo seu índice (0 e 1).
const json1String =$('AI Agent').first().json.output.replaceAll('```json','').replaceAll('```','').trim(); 
const json2String = $input.all(0)[0].json.output.replaceAll('```json','').replaceAll('```','').trim();

try {
  // Converte as strings JSON em arrays de objetos JavaScript.
  const array1 = JSON.parse(json1String);
  const array2 = JSON.parse(json2String);

  // Mescla (concatena) os dois arrays em um só.
  const mergedArray = array1.concat(array2);

  // Mapeia o array mesclado para o formato de saída padrão,
  // onde cada objeto do array se torna um item na saída do nó.
  return mergedArray.map(item => ({ json: item }));

} catch (error) {
  // Retorna uma mensagem de erro detalhada caso uma das strings não seja um JSON válido.
  return [{ 
    json: { 
      error: "Erro ao processar e mesclar os JSONs.",
      errorMessage: error.message,
      // Inclui os dados originais para facilitar a depuração.
      json1_received: json1String,
      json2_received: json2String
    } 
  }];
}
